var TaskList = [];

function addTask(duration, task) //duration of task in seconds. Task is the topic.
{
    TaskList.push({Task:task, State:'1', Duration:duration});
    TaskList.push({Task:task, State:'0', Duration:0});
}

function doTaskList()
{
    if (TaskList.length > 0)
    {
        var currentTask = TaskList[0];
        //node.warn("Executing Task:"+currentTask.Task+" - State:"+currentTask.State+" - Duration:"+currentTask.Duration);
        msg.topic=currentTask.Task;
        msg.payload=currentTask.State;
        node.send(msg);
        //Remove task
        TaskList.splice(0,1);
        setTimeout(doTaskList, 1000*60*currentTask.Duration*context.global.ZoneTime+10);
    } else {
        node.warn("TaskList is Empty");
    }
}

