var devicename = "sdm220"; // Device name used for context variable
var system_id = 3; // System id number for diagnostic update
var online_threshold = 10; // Seconds between updates under which the device is considered online
var offline_threshold = 30; // Seconds between updates above which the device is considered offline

var temp = context.get(devicename+"_update");
var current = new Date();
msg.payload = "No data";
msg.warning = false;
if (msg.topic!=="timecheck") {
    // Do not update the context if it is triggered by the check inject node
    context.set(devicename+"_update",current);
}
if (temp===undefined) {
    // this will be the case when node-red is booting up or redeployed
    context.set(devicename+"_update",current);
}

if (temp!==undefined) {
    current = current - temp;
    current = Math.floor(current/1000);
    var minute = Math.floor(current/60);
    var hour = Math.floor(minute/60);
    var day = Math.floor(hour/24);
    if (current>24*60*60) {
        msg.payload = "Last update " + day + " days, " + hour%24 + " hours, " + minute%60 + " minutes, " + current%60 + " seconds ago";
    } else if (current>60*60) {
        msg.payload = "Last update " + hour%24 + " hours, " + minute%60 + " minutes, " + current%60 + " seconds ago";
    } else if (current>60) {
        msg.payload = "Last update " + minute%60 + " minutes, " + current%60 + " seconds ago";
    } else {
        msg.payload = "Last update " + current%60 + " seconds ago";
    }

    if (context.get(devicename+"_state")!==1) {
        if (current<online_threshold) {
            msg.payload = "SDM220 is now online";
            msg.system = system_id; // System id, use 1 for Dummy
            msg.state = 1; // specify if the message is to change system status
            msg.severity = 0; // 0: information, 1: warning, 2: error
            //msg.email = true; // if separate email should be sent
            //msg.emailtext = ""; this a long text which goes into the email  
            msg.warning = true;
            context.set(devicename+"_state",1);
        }
    } else {
        if (current>offline_threshold) {
            msg.payload = "SDM220 is not transmitting";
            msg.system = system_id; // System id, use 1 for Dummy
            msg.state = 99; // specify if the message is to change system status
            msg.severity = 2; // 0: information, 1: warning, 2: error
            //msg.email = true; // if separate email should be sent
            //msg.emailtext = ""; this a long text which goes into the email            
            msg.warning = true;
            context.set(devicename+"_state",99);
        }
    }


}

